mapper.select:
SELECT TO_CHAR(c.co_start_date,'YYYY-MM-DD') AS co_start_date ,TO_CHAR(c.end_date,'YYYY-MM-DD')AS end_date ,c.co_ing_status ,c.co_release ,c.co_disease ,a.d_name ,d.ma_name ,c.co_no ,e.p_name ,a.d_id ,a.d_pic FROM doctor a ,co_med c ,major d ,patient e WHERE a.d_id = c.co_doc AND a.ma_no = d.ma_no AND c.p_no = e.p_no
and a.d_name = #{dName}
and c.co_disease = #{coDisease}
and p_name like '%'||#{keyword}||'%'
and a.d_name =#{dName} or c.co_disease=#{coDisease} or a.p_name = #{pName}
and a.d_id = #{dId}
order by co_start_date DESC

SELECT COUNT(*) FROM (SELECT e.d_id ,a.p_name ,b.co_disease ,c.sub_d_name ,b.co_doc ,b.end_reason ,e.d_name ,d.ma_name ,TO_CHAR(b.co_start_date,'yyyy-mm-dd hh24:mi') AS co_start_date ,TO_CHAR(b.end_date,'yyyy-mm-dd hh24:mi') AS end_date ,b.co_no ,e.d_pic FROM patient a ,co_med b ,co_sub_d c ,major d ,doctor e ,com_reco F WHERE a.p_no = b.p_no AND e.d_id = b.co_doc AND d.ma_no = e.ma_no AND b.co_no = F.co_no AND b.co_no = c.co_no(+) AND e.d_id = #{dId}
and a.d_name = #{dName}
and c.co_disease = #{coDisease}
and a.d_name =#{dName} or c.co_disease=#{coDisease}
ORDER BY co_start_date DESC )

SELECT COUNT(*) FROM( SELECT TO_CHAR(c.co_start_date,'YYYY-MM-DD') AS co_start_date ,TO_CHAR(c.end_date,'YYYY-MM-DD')AS end_date ,c.co_ing_status ,c.co_release ,c.co_disease ,a.d_name ,d.ma_name ,c.co_no ,e.p_name ,c.co_no ,a.d_id ,a.d_pic FROM doctor a ,co_med c ,major d ,patient e WHERE a.d_id = c.co_doc AND a.ma_no = d.ma_no AND c.p_no = e.p_no
and a.d_name = #{dName}
and c.co_disease = #{coDisease}
and a.d_name =#{dName} or c.co_disease=#{coDisease}
order by co_start_date DESC )

select count(*) from( select to_char(C.co_start_date,'YYYY-MM-DD')as co_start_date, C.co_release, C.co_disease, C.co_no, E.p_name, a.d_id, A.d_pic, A.d_name, D.ma_name from doctor A, co_med C, major D, patient E where A.d_id=C.CO_DOC and A.ma_no=D.ma_no and c.p_no = e.p_no and C.co_ing_status='1'
and a.d_name = #{dName}
and c.co_disease = #{coDisease}
and a.d_name =#{dName} or c.co_disease=#{coDisease}
order by C.co_start_date DESC )

select count(*) from( select to_char(C.co_open_date,'YYYY-MM-DD')as co_open_date, to_char(C.end_date,'YYYY-MM-DD')as end_date, C.co_release, C.co_disease, C.co_no, a.d_id, A.d_pic, A.d_name, D.ma_name from doctor A, co_med C, major D where A.d_id=C.CO_DOC and A.ma_no=D.ma_no and c.end_date is not null and C.co_ing_status='2'
and a.d_name = #{dName}
and c.co_disease = #{coDisease}
and a.d_name =#{dName} or c.co_disease=#{coDisease}
order by C.co_open_date DESC) c where c.co_no is not null

SELECT c.co_no ,TO_CHAR(c.co_open_date,'YYYY-MM-DD') AS co_open_date ,TO_CHAR(c.end_date,'YYYY-MM-DD') AS end_date ,c.co_ing_status ,c.co_release ,c.co_disease ,f.p_name ,a.d_name ,a.d_id ,d.ma_name ,c.co_doc ,a.d_pic FROM doctor a ,co_med c ,major d ,patient f ,co_doctor g WHERE a.d_id = g.d_id AND a.ma_no = d.ma_no AND c.p_no = f.p_no and c.co_no = g.co_no and c.co_no in (select h.co_no from co_med h, co_doctor i where h.co_no = i.co_no
and i.d_id = #{dId}
and i.paty_yn = 'Y' and h.co_ing_status= '1')
and c.co_doc = #{dId}
ORDER BY c.co_open_date DESC

SELECT COUNT(*) FROM (SELECT c.co_no ,TO_CHAR(c.co_open_date,'YYYY-MM-DD') AS co_open_date ,TO_CHAR(c.end_date,'YYYY-MM-DD') AS end_date ,c.co_ing_status ,c.co_release ,c.co_disease ,f.p_name ,a.d_name ,a.d_id ,d.ma_name ,c.co_doc ,a.d_pic FROM doctor a ,co_med c ,major d ,patient f ,co_doctor g WHERE a.d_id = g.d_id AND a.ma_no = d.ma_no AND c.p_no = f.p_no and c.co_no = g.co_no and c.co_no in (select h.co_no from co_med h, co_doctor i where h.co_no = i.co_no
and i.d_id = #{dId}
and i.paty_yn = 'Y' and h.co_ing_status= '1')
and c.co_doc = #{dId}
ORDER BY c.co_open_date DESC)

SELECT COUNT(*) FROM( SELECT c.co_no ,TO_CHAR(c.co_open_date,'YYYY-MM-DD') AS co_open_date ,TO_CHAR(c.end_date,'YYYY-MM-DD') AS end_date ,c.co_ing_status ,c.co_release ,c.co_disease ,f.p_name ,a.d_name ,a.d_id ,d.ma_name ,c.co_doc ,a.d_pic FROM doctor a ,co_med c ,major d ,patient f ,co_doctor g WHERE a.d_id = g.d_id AND a.ma_no = d.ma_no AND c.p_no = f.p_no and c.co_no = g.co_no and c.end_date is not null and c.co_no in (select h.co_no from co_med h, co_doctor i where h.co_no = i.co_no
and i.d_id = #{dId}
and i.paty_yn = 'Y' and h.co_ing_status= '2')
and c.co_doc = #{dId}
ORDER BY c.co_open_date DESC )

SELECT c.co_no ,TO_CHAR(c.co_open_date,'YYYY-MM-DD') AS co_open_date ,TO_CHAR(c.end_date,'YYYY-MM-DD') AS end_date ,c.co_ing_status ,c.co_release ,c.co_disease ,f.p_name ,a.d_name ,a.d_id ,d.ma_name ,c.co_doc ,a.d_pic FROM doctor a ,co_med c ,major d ,patient f ,co_doctor g WHERE a.d_id = g.d_id AND a.ma_no = d.ma_no AND c.p_no = f.p_no and c.co_no = g.co_no and c.end_date is not null and c.co_no in (select h.co_no from co_med h, co_doctor i where h.co_no = i.co_no
and i.d_id = #{dId}
and i.paty_yn = 'Y' and h.co_ing_status= '2')
and c.co_doc = #{dId}
ORDER BY c.co_open_date DESC
